package com.JavaProgram;

public class Loopfor {
	
	
	public void ffor() {
		
		for(int i=0;i<=10;i++) {
			System.out.println("*"+"  "+i);
		}
		System.out.println("Loop Fails");
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
Loopfor value=new Loopfor();
value.ffor();

	}

}
